import React, { useState, useEffect } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faChevronLeft, faChevronRight } from "@fortawesome/free-solid-svg-icons";
import { Formik } from "formik";
import { useTranslation } from "react-i18next";
// import FontAwesome from "react-fontawesome";
import "../App.css";
import LanguageSwitcher from "./LanguageSwitcher";
import { DayCell } from "../components/DayCell";
import { DayHeader } from "../components/DayHeader";
import { EventModal } from "../components/EventModal";
import {
  getDaysInMonth,
  nextMonth,
  prevMonth,
  chunkArray,
} from "../utils/dateFunctions";

const CalenderScliderByMonth = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedDay, setSelectedDay] = useState(null);
  const [textarea, setTextArea] = useState("");
  const [currentDate, setCurrentDate] = useState(new Date());
  const [eventData, setEventData] = useState(
    JSON.parse(localStorage.getItem("eventData")) || []
  );

  useEffect(() => {
    localStorage.setItem("eventData", JSON.stringify(eventData));
  }, [eventData]);

  const { t } = useTranslation();

  const daysOfWeek = [
    t("Sunday"),
    t("Monday"),
    t("Tuesday"),
    t("Wednesday"),
    t("Thursday"),
    t("Friday"),
    t("Saturday"),
  ];

  const handleDayDoubleClick = (day) => {
    setSelectedDay(day);
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setSelectedDay(null);
  };

  const days = getDaysInMonth(currentDate);
  const weeks = chunkArray(days, 7);

  // return (
    // <div>
    //   <LanguageSwitcher />
    //   <div className="flex justify-between items-center py-5 px-10">
    //     <button onClick={() => prevMonth(currentDate, setCurrentDate)}>
    //       <FontAwesomeIcon icon={faChevronLeft} />
    //     </button>
    //     <h1 className="text-xl font-bold">
    //       {`${currentDate.toLocaleString("default", {
    //         month: "long",
    //       })} ${currentDate.getFullYear()}`}
    //     </h1>
    //     <button onClick={() => nextMonth(currentDate, setCurrentDate)}>
    //       <FontAwesomeIcon icon={faChevronRight} />
    //     </button>
    //   </div>

    //   < className="container mx-auto mt-10">
    //     <table className="w-full">
    //       <DayHeader daysOfWeek={daysOfWeek} />
    //       <tbody>
    //         {weeks.map((week, weekIndex) => (
    //           <tr key={weekIndex} className="text-center h-20">
    //             {week.map((day) => (
    //               <DayCell
    //                 key={day}
    //                 day={day}
    //                 currentDate={currentDate}
    //                 onDoubleClick={handleDayDoubleClick}
    //                 eventData={eventData}
    //               />
    //             ))}
    //           </tr>
    //         ))}
    //       </tbody>
    //     </table>
    return (
      <div className="bg-gray-100 min-h-screen">
        <LanguageSwitcher />
        <div className="flex justify-between items-center py-4 px-6 bg-white shadow-md">
          <button onClick={() => prevMonth(currentDate, setCurrentDate)} className="text-xl">
            <FontAwesome name="chevron-left" />
          </button>
          <h1 className="text-2xl font-semibold">{`${translatedMonthName} ${currentDate.getFullYear()}`}</h1>
          <button onClick={() => nextMonth(currentDate, setCurrentDate)} className="text-xl">
            <FontAwesome name="chevron-right" />
          </button>
        </div>
        <div className="mt-10">
          <DayHeader daysOfWeek={daysOfWeek} />
          <div className="bg-white shadow-md rounded-lg p-4">
            {weeks.map((week, weekIndex) => (
              <div key={weekIndex} className="flex">
                {week.map((day) => (
                  <DayCell
                    key={day}
                    day={day}
                    currentDate={currentDate}
                    onDoubleClick={handleDayDoubleClick}
                    eventData={eventData}
                  />
                ))}
              </div>
            ))}
          </div>
        <EventModal
          isOpen={isModalOpen}
          onClose={closeModal}
          selectedDay={selectedDay}
          eventData={eventData}
          setEventData={setEventData}
          Formik={Formik}
          textarea={textarea}
          setTextArea={setTextArea}
          currentDate={currentDate}
        />
      </div>
    </div>

  );
};

export default CalenderScliderByMonth;
