export const DayHeader = ({ daysOfWeek }) => (
  <div className="bg-white shadow-md rounded-t-lg py-2 mb-2">
    <div className="flex">
      {daysOfWeek.map((day, index) => (
        <div key={index} className="flex-grow text-center p-4 border-r">
          <span className="text-sm text-gray-600">{day}</span>
        </div>
      ))}
