import React, { useState, useEffect } from "react";
import { useTranslation } from "react-i18next";
import { Formik } from "formik";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faChevronLeft,
  faChevronRight,
} from "@fortawesome/free-solid-svg-icons";
import "../App.css";
import LanguageSwitcher from "./LanguageSwitcher";
import { DayCell } from "../components/DayCell";
import { DayHeader } from "../components/DayHeader";
import { EventModal } from "../components/EventModal";
import {
  getDaysInMonth,
  nextMonth,
  prevMonth,
  chunkArray,
} from "../utils/dateFunctions";

const CalenderScliderByMonth = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedDay, setSelectedDay] = useState(null);
  const [textarea, setTextArea] = useState("");
  const [currentDate, setCurrentDate] = useState(new Date());
  const [eventData, setEventData] = useState(
    JSON.parse(localStorage.getItem("eventData")) || []
  );

  useEffect(() => {
    localStorage.setItem("eventData", JSON.stringify(eventData));
  }, [eventData]);

  useEffect(() => {
    document.title = `${currentDate.toLocaleString("default", {
      month: "long",
      year: "numeric",
    })}`;
  }, [currentDate]);

  const { t } = useTranslation();

  const daysOfWeek = [
    t("Sunday"),
    t("Monday"),
    t("Tuesday"),
    t("Wednesday"),
    t("Thursday"),
    t("Friday"),
    t("Saturday"),
  ];
  const handletextareaChange = (e) => {
    setTextArea(e.target.value);
  };

  const handleDayDoubleClick = (day) => {
    setSelectedDay(day);
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setSelectedDay(null);
  };

  const days = Array.from(
    {
      length: getDaysInMonth(currentDate.getMonth(), currentDate.getFullYear()),
    },
    (_, i) => i + 1
  );

  const monthName = currentDate.toLocaleString("default", { month: "long" });
  const translatedMonthName = t(`${monthName}`);

  const weeks = chunkArray(days, 7);

  return (
    <div className="bg-gray-200 min-h-screen p-4">
      <LanguageSwitcher />
      <div className="flex justify-between items-center py-4 px-6 bg-white shadow-md rounded">
        <button
          onClick={() => prevMonth(currentDate, setCurrentDate)}
          className="text-xl"
        >
          <FontAwesomeIcon icon={faChevronLeft} />
        </button>
        <h1 className="text-2xl font-semibold">{`${translatedMonthName} ${currentDate.getFullYear()}`}</h1>
        <button
          onClick={() => nextMonth(currentDate, setCurrentDate)}
          className="text-xl"
        >
          <FontAwesomeIcon icon={faChevronRight} />
        </button>
      </div>
      <div className="mt-10 bg-white shadow-md rounded-lg p-4">
        <DayHeader daysOfWeek={daysOfWeek} />
        {weeks.map((week, weekIndex) => (
          <div key={weekIndex} className="flex justify-between">
            {week.map((day) => (
              <DayCell
                key={day}
                day={day}
                currentDate={currentDate}
                onDoubleClick={handleDayDoubleClick}
                eventData={eventData}
              />
            ))}
          </div>
        ))}
      </div>
      <EventModal
        isOpen={isModalOpen}
        onClose={closeModal}
        selectedDay={selectedDay}
        eventData={eventData}
        setEventData={setEventData}
        Formik={Formik}
        textarea={textarea}
        setTextArea={setTextArea}
        currentDate={currentDate}
      />
    </div>
  );
};

export default CalenderScliderByMonth;
