import React, { useState, useEffect } from "react";
import FontAwesome from "react-fontawesome";
import * as formik from "formik";
import "../App.css";
import { useTranslation } from "react-i18next";
import LanguageSwitcher from "./LanguageSwitcher";
import { DayHeader } from "../components/DayHeader";
import { EventModal } from "../components/EventModal";
import {
  getDaysInMonth,
  nextMonth,
  prevMonth,
  chunkArray,
} from "../utils/dateFunctions";
const { Formik } = formik;

const CalenderScliderByDay = () => {
  const { t } = useTranslation();
  const daysOfWeek = [
    t("Sunday"),
    t("Monday"),
    t("Tuesday"),
    t("Wednesday"),
    t("Thursday"),
    t("Friday"),
    t("Saturday"),
  ];

  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedDay, setSelectedDay] = useState(null);
  const [textarea, setTextArea] = useState("");
  const [currentDate, setCurrentDate] = useState(new Date());

  const [eventData, setEventData] = useState(
    JSON.parse(localStorage.getItem("eventData")) || []
  );

  useEffect(() => {
    localStorage.setItem("eventData", JSON.stringify(eventData));
  }, [eventData]);


//   useEffect(() => {
//     localStorage.setItem("eventData", JSON.stringify(eventData));
// }, [eventData]);


  const handletextareaChange = (e) => {
    setTextArea(e.target.value);
  };

  const handleDayDoubleClick = (day) => {
    setSelectedDay(day);
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setSelectedDay(null);
  };

  useEffect(() => {
    document.title = `${currentDate.toLocaleString("default", {
      month: "long",
      year: "numeric",
    })}`;
  }, [currentDate]);

  const days = Array.from(
    {
      length: getDaysInMonth(currentDate.getMonth(), currentDate.getFullYear()),
    },
    (_, i) => i + 1
  );

  const monthName = currentDate.toLocaleString("default", { month: "long" });
  const translatedMonthName = t(`${monthName}`);

  const weeks = chunkArray(days, 7);
  return (
    <div>
      <LanguageSwitcher />
      <div className="buttons flex justify-around h-600 p-500 ">
      <button
          class="p-1"
          onClick={() => prevMonth(currentDate, setCurrentDate)}
        >
          <FontAwesome name="chevron-left" className="-ml-px" />
        </button>
        <button
          class="p-1"
          onClick={() => nextMonth(currentDate, setCurrentDate)}
        > <FontAwesome name="chevron-right" className="-ml-px" />

          </button>
      </div>
      <div class="container mx-auto mt-10">
        <div class="">
          <div class="header flex justify-between border-b p-2">
            <span class="text-lg font-bold">
              {" "}
              <span class="text-lg font-bold">
                {`${translatedMonthName} ${currentDate.getFullYear()}`}
              </span>
            </span>
            <div class="buttons">
              <button class="p-1">
                <path
                  fill-rule="evenodd"
                  d="M8 15A7 7 0 1 0 8 1a7 7 0 0 0 0 14zm0 1A8 8 0 1 0 8 0a8 8 0 0 0 0 16z"
                />
                <path
                  fill-rule="evenodd"
                  d="M8.354 11.354a.5.5 0 0 0 0-.708L5.707 8l2.647-2.646a.5.5 0 1 0-.708-.708l-3 3a.5.5 0 0 0 0 .708l3 3a.5.5 0 0 0 .708 0z"
                />
                <path
                  fill-rule="evenodd"
                  d="M11.5 8a.5.5 0 0 0-.5-.5H6a.5.5 0 0 0 0 1h5a.5.5 0 0 0 .5-.5z"
                />
              </button>
              <button class="p-1">
                <path
                  fill-rule="evenodd"
                  d="M8 15A7 7 0 1 0 8 1a7 7 0 0 0 0 14zm0 1A8 8 0 1 0 8 0a8 8 0 0 0 0 16z"
                />
                <path
                  fill-rule="evenodd"
                  d="M7.646 11.354a.5.5 0 0 1 0-.708L10.293 8 7.646 5.354a.5.5 0 1 1 .708-.708l3 3a.5.5 0 0 1 0 .708l-3 3a.5.5 0 0 1-.708 0z"
                />
                <path
                  fill-rule="evenodd"
                  d="M4.5 8a.5.5 0 0 1 .5-.5h5a.5.5 0 0 1 0 1H5a.5.5 0 0 1-.5-.5z"
                />
              </button>
            </div>
          </div>

          <table class="w-full">
            <DayHeader daysOfWeek={daysOfWeek} />
            <tbody>
              {weeks.map((week, weekIndex) => (
                <tr key={weekIndex} className="text-center h-20">
                  {week.map((day) => (
                    <DayCell
                      key={day}
                      day={day}
                      currentDate={currentDate}
                      onDoubleClick={handleDayDoubleClick}
                      eventData={eventData}
                    />
                  ))}
                </tr>
              ))}
            </tbody>
          </table>

          <EventModal
            isOpen={isModalOpen}
            onClose={closeModal}
            selectedDay={selectedDay}
            eventData={eventData}
            setEventData={setEventData}
            Formik={Formik}
            textarea={textarea}
            setTextArea={setTextArea}
            handletextareaChange={handletextareaChange}
            currentDate={currentDate}
          />
        </div>
      </div>
    </div>
  );
};
//
const DayCell = ({ day, currentDate, onDoubleClick, eventData }) => {
  // Using fullDate logic to find the event for the day
  const fullDate = `${currentDate.getFullYear()}-${String(
    currentDate.getMonth() + 1
  ).padStart(2, "0")}-${String(day).padStart(2, "0")}`;
  const dayEvent = eventData.find((event) => event.fullDate === fullDate);

  // Check if the current cell is for today's date
  const isToday = currentDate.getDate() === day;

  // If it's not today, make it invisible
  if (!isToday) return <td className="invisible"></td>;

  return (
    <td
      className="border p-1 h-40 xl:w-40 lg:w-30 md:w-30 sm:w-20 w-10 overflow-auto transition cursor-pointer duration-500 ease bg-red-300"
      onDoubleClick={() => onDoubleClick(day)}
    >
      <div className="flex flex-col h-40 mx-auto xl:w-40 lg:w-30 md:w-30 sm:w-full w-10 mx-auto overflow-hidden">
        <div className="top h-5 w-full">
          <div className="rounded-full bg-red-300">{day}</div>
        </div>

        <div className="bottom flex-grow h-30 py-1 w-full cursor-pointer">
          {dayEvent && (
            <div className="rounded-full bg-indigo-300 ">
              <strong>{dayEvent.eventname}</strong>
              <p>{dayEvent.description}</p>
              <small>
                {dayEvent.ffrom} - {dayEvent.to}
              </small>
            </div>
          )}
        </div>
      </div>
    </td>
  );
};


export default CalenderScliderByDay;
